import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Layout from '../components/Layout/Layout';
import './Home.css';

// Main Home Component
function Home() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const featureRefs = useRef([]);
  const navigate = useNavigate();

  // Enhanced slider data
  const sliderData = [
    {
      id: 1,
      title: "Excellence in Education",
      subtitle: "Nurturing young minds for a brighter future",
      image: "https://cdn.pixabay.com/photo/2022/03/04/13/01/school-children-7047286_1280.jpg",
      cta: "Discover Our Programs"
    },
    {
      id: 2,
      title: "Modern Learning Environment",
      subtitle: "State-of-the-art facilities for optimal learning",
      image: "https://images.unsplash.com/photo-1591123120675-6f7f1aae0e5b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1800&q=80",
      cta: "Tour Our Campus"
    },
    {
      id: 3,
      title: "Building Community",
      subtitle: "Where students grow together",
      image: "https://images.unsplash.com/photo-1577896851231-70ef18881754?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1800&q=80",
      cta: "Join Our Community"
    }
  ];

  // Features data
  const featuresData = [
    {
      id: 'academic-excellence',
      title: 'Academic Excellence',
      description: 'Our curriculum is designed to challenge students and foster critical thinking skills.',
      image: 'https://images.unsplash.com/photo-1501504905252-473c47e087f8?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80',
      hue: '210',
      fullDetails: {
        image: 'https://images.unsplash.com/photo-1523050854058-8df90110c9f1?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1800&q=80',
        description: 'Our school has a long-standing tradition of academic excellence. We offer a rigorous curriculum that challenges students while providing the support they need to succeed. Our dedicated teachers use innovative teaching methods to ensure that every student reaches their full potential.',
        achievements: [
          '98% college acceptance rate',
          '15 National Merit Scholars in the past year',
          'Average SAT score of 1350',
          'Advanced Placement courses in 12 subjects'
        ],
        schedule: [
          { day: 'Monday - Friday', time: '8:00 AM - 3:00 PM' },
          { day: 'After-school Programs', time: '3:15 PM - 5:00 PM' },
          { day: 'Weekend Activities', time: '9:00 AM - 12:00 PM' }
        ],
        photos: [
          'https://images.unsplash.com/photo-1523050854058-8df90110c9f1?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80',
          'https://images.unsplash.com/photo-1498243691581-b145c3f54a5a?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80',
          'https://images.unsplash.com/photo-1571260899304-425eee4c7efc?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80'
        ]
      }
    },
    {
      id: 'sports-programs',
      title: 'Sports Programs',
      description: 'We offer a wide range of athletic activities to promote physical health and teamwork.',
      image: 'https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80',
      hue: '190',
      fullDetails: {
        image: 'https://images.unsplash.com/photo-1549060279-7e168fce7090?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1800&q=80',
        description: 'Our sports programs are designed to develop physical fitness, teamwork, and sportsmanship. We offer a variety of competitive and recreational sports for all skill levels. Our state-of-the-art facilities include a gymnasium, swimming pool, tennis courts, and athletic fields.',
        achievements: [
          'State champions in basketball for 3 consecutive years',
          'Regional swimming competition winners',
          '15 students awarded athletic scholarships last year',
          'Partnership with local professional teams for coaching'
        ],
        schedule: [
          { day: 'Basketball Practice', time: 'Monday & Wednesday, 4:00 PM - 6:00 PM' },
          { day: 'Swimming', time: 'Tuesday & Thursday, 3:30 PM - 5:30 PM' },
          { day: 'Soccer', time: 'Monday - Friday, 4:00 PM - 6:00 PM' },
          { day: 'Track & Field', time: 'Tuesday & Thursday, 3:30 PM - 5:30 PM' }
        ],
        photos: [
          'https://images.unsplash.com/photo-1549060279-7e168fce7090?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80',
          'https://images.unsplash.com/photo-1552667466-07770ae110d0?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80',
          'https://images.unsplash.com/photo-1571019614244-c5d476cbae22?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80'
        ]
      }
    },
    {
      id: 'arts-culture',
      title: 'Arts & Culture',
      description: 'Our arts programs encourage creativity and self-expression in various mediums.',
      image: 'https://images.unsplash.com/photo-1541961017774-22349e4a1262?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80',
      hue: '220',
      fullDetails: {
        image: 'https://images.unsplash.com/photo-1567942712661-82b9b407abbf?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1800&q=80',
        description: 'Our arts programs provide students with opportunities to explore their creativity and develop their talents. We offer courses in visual arts, music, theater, and dance. Our students regularly participate in exhibitions, performances, and competitions.',
        achievements: [
          'First place in state art competition for 5 years running',
          'Annual theater production with sold-out shows',
          'Student artwork displayed in local galleries',
          'Music ensemble invited to perform at regional festival'
        ],
        schedule: [
          { day: 'Art Club', time: 'Monday, 3:30 PM - 5:00 PM' },
          { day: 'Drama Rehearsals', time: 'Tuesday & Thursday, 4:00 PM - 6:00 PM' },
          { day: 'Music Lessons', time: 'Wednesday, 3:30 PM - 5:30 PM' },
          { day: 'Cultural Events', time: 'Monthly, dates vary' }
        ],
        photos: [
          'https://images.unsplash.com/photo-1567942712661-82b9b407abbf?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80',
          'https://images.unsplash.com/photo-1508700929628-666bc8bd84ea?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80',
          'https://images.unsplash.com/photo-1513475382585-d06e58bcb0e0?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80'
        ]
      }
    },
    {
      id: 'inclusive-community',
      title: 'Inclusive Community',
      description: 'We celebrate diversity and foster an environment of respect and inclusion.',
      image: 'https://images.unsplash.com/photo-1523580494863-6f3031224c94?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80',
      hue: '200',
      fullDetails: {
        image: 'https://images.unsplash.com/photo-1591123120675-6f7f1aae0e5b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1800&q=80',
        description: 'We believe that diversity is our strength. Our inclusive community welcomes students from all backgrounds and fosters an environment of mutual respect and understanding. We offer various programs and initiatives to promote diversity, equity, and inclusion.',
        achievements: [
          'Recipient of the National Diversity Award for 3 consecutive years',
          'Student-led inclusion initiatives impacting the local community',
          'Partnerships with international schools for cultural exchange',
          'Scholarship programs for underrepresented students'
        ],
        schedule: [
          { day: 'Diversity Club Meetings', time: 'Every Friday, 3:30 PM - 4:30 PM' },
          { day: 'Community Service Projects', time: 'Monthly, dates vary' },
          { day: 'Cultural Celebration Events', time: 'Quarterly, dates vary' },
          { day: 'Parent Diversity Committee', time: 'First Tuesday of each month, 6:00 PM' }
        ],
        photos: [
          'https://images.unsplash.com/photo-1591123120675-6f7f1aae0e5b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80',
          'https://images.unsplash.com/photo-1577896851231-70ef18881754?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80',
          'https://images.unsplash.com/photo-1523240795612-9a054b0db644?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80'
        ]
      }
    }
  ];

  // New sections data
  const upcomingEvents = [
    {
      id: 1,
      title: "Annual Science Fair",
      date: "Oct 15, 2023",
      time: "9:00 AM - 3:00 PM",
      location: "School Gymnasium",
      description: "Students showcase their innovative science projects",
      fullDetails: {
        image: "https://images.unsplash.com/photo-1569864358643-800a5fe69d18?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1800&q=80",
        description: "Our Annual Science Fair showcases the incredible scientific talents of our students. This year's theme is 'Innovations for a Sustainable Future'. Students from all grades will present projects ranging from renewable energy solutions to environmental conservation techniques. Judges from local universities and tech companies will evaluate the projects and award prizes in various categories.",
        schedule: [
          { time: "9:00 AM", activity: "Opening Ceremony" },
          { time: "9:30 AM - 12:00 PM", activity: "Project Viewing & Judging" },
          { time: "12:00 PM - 1:00 PM", activity: "Lunch Break" },
          { time: "1:00 PM - 2:30 PM", activity: "Public Viewing" },
          { time: "2:30 PM - 3:00 PM", activity: "Award Ceremony" }
        ],
        photos: [
          "https://images.unsplash.com/photo-1569864358643-800a5fe69d18?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80",
          "https://images.unsplash.com/photo-1532094349884-543bc11b234d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80",
          "https://images.unsplash.com/photo-1554475901-4538ddfbccc2?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80"
        ]
      }
    },
    {
      id: 2,
      title: "Parent-Teacher Conferences",
      date: "Oct 20-21, 2023",
      time: "4:00 PM - 7:00 PM",
      location: "Main Building",
      description: "Opportunity to discuss student progress with teachers",
      fullDetails: {
        image: "https://images.unsplash.com/photo-1571260899304-425eee4c7efc?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1800&q=80",
        description: "Parent-Teacher Conferences provide an excellent opportunity for parents to meet with teachers and discuss their child's academic progress, strengths, and areas for improvement. These one-on-one meetings help foster a collaborative relationship between home and school, ensuring the best possible educational experience for each student.",
        schedule: [
          { time: "October 20, 4:00 PM - 7:00 PM", activity: "Conference Sessions" },
          { time: "October 21, 9:00 AM - 12:00 PM", activity: "Conference Sessions" },
          { time: "October 21, 1:00 PM - 4:00 PM", activity: "Make-up Sessions" }
        ],
        photos: [
          "https://images.unsplash.com/photo-1571260899304-425eee4c7efc?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80",
          "https://images.unsplash.com/photo-1584697964358-3e14ca57658b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80",
          "https://images.unsplash.com/photo-1577896851231-70ef18881754?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80"
        ]
      }
    },
    {
      id: 3,
      title: "Fall Festival",
      date: "Nov 5, 2023",
      time: "11:00 AM - 4:00 PM",
      location: "School Grounds",
      description: "Fun activities, food, and games for the whole family",
      fullDetails: {
        image: "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1800&q=80",
        description: "Our annual Fall Festival is a beloved tradition that brings together students, families, teachers, and the community for a day of fun, food, and festivities. Enjoy carnival games, pumpkin decorating, hayrides, live music, and delicious food from local vendors. All proceeds support our school's extracurricular programs.",
        schedule: [
          { time: "11:00 AM", activity: "Festival Opens" },
          { time: "11:30 AM", activity: "Pumpkin Decorating Contest" },
          { time: "1:00 PM", activity: "Live Music Performance" },
          { time: "2:30 PM", activity: "Costume Parade" },
          { time: "4:00 PM", activity: "Festival Closes" }
        ],
        photos: [
          "https://images.unsplash.com/photo-1506905925346-21bda4d32df4?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80",
          "https://images.unsplash.com/photo-1472653525502-fc569e405a74?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80",
          "https://images.unsplash.com/photo-1507041957456-9c397ce5c5d2?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80"
        ]
      }
    }
  ];

  const newsUpdates = [
    {
      id: 1,
      title: "School Ranked #1 in State",
      date: "Sept 10, 2023",
      excerpt: "Our school has been ranked first in the state for academic excellence for the fifth consecutive year.",
      image: "https://images.unsplash.com/photo-1584697964358-3e14ca57658b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80",
      fullDetails: {
        content: "We are thrilled to announce that our school has been ranked #1 in the state for academic excellence for the fifth consecutive year. This remarkable achievement reflects the hard work and dedication of our students, teachers, and staff. Our comprehensive curriculum, innovative teaching methods, and supportive learning environment have consistently produced outstanding results. This recognition validates our commitment to providing the highest quality education and preparing students for success in college and beyond.",
        author: "Dr. James Wilson, Principal",
        tags: ["Achievement", "Academic Excellence", "Ranking"]
      }
    },
    {
