import React, { useState } from 'react';
import './MyAccount.css';

const MyAccount = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [user, setUser] = useState(null);
  const [form, setForm] = useState({ 
    username: '', 
    password: '',
    email: '',
    firstName: '',
    lastName: '',
    rememberMe: false
  });
  const [isRegistering, setIsRegistering] = useState(false);
  const [activeTab, setActiveTab] = useState('profile');

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setForm({ 
      ...form, 
      [name]: type === 'checkbox' ? checked : value 
    });
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (form.username && form.password) {
      setIsLoggedIn(true);
      setUser({ 
        username: form.username,
        email: 'user@example.com',
        firstName: 'John',
        lastName: 'Doe',
        joinDate: 'January 15, 2023',
        orders: 12
      });
      setForm({ 
        username: '', 
        password: '',
        email: '',
        firstName: '',
        lastName: '',
        rememberMe: false
      });
    } else {
      alert('Please enter valid credentials.');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUser(null);
  };

  const handleRegister = (e) => {
    e.preventDefault();
    if (form.username && form.password && form.email && form.firstName && form.lastName) {
      alert(`User ${form.username} registered successfully!`);
      setIsRegistering(false);
      setForm({ 
        username: '', 
        password: '',
        email: '',
        firstName: '',
        lastName: '',
        rememberMe: false
      });
    } else {
      alert('Please fill all required fields.');
    }
  };

  const toggleFormMode = () => {
    setIsRegistering(!isRegistering);
    setForm({ 
      username: '', 
      password: '',
      email: '',
      firstName: '',
      lastName: '',
      rememberMe: false
    });
  };

  return (
    <div className="my-account-container">
      <div className="account-card">
        {!isLoggedIn ? (
          <div className="auth-section">
            <div className="auth-header">
              <h2>{isRegistering ? 'Create Account' : 'Welcome Back'}</h2>
              <p>{isRegistering ? 'Join us today' : 'Sign in to your account'}</p>
            </div>
            
            <form onSubmit={isRegistering ? handleRegister : handleLogin}>
              {isRegistering && (
                <div className="form-row">
                  <div className="input-group">
                    <label>First Name</label>
                    <input
                      type="text"
                      name="firstName"
                      placeholder="Enter your first name"
                      value={form.firstName}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                  <div className="input-group">
                    <label>Last Name</label>
                    <input
                      type="text"
                      name="lastName"
                      placeholder="Enter your last name"
                      value={form.lastName}
                      onChange={handleInputChange}
                      required
                    />
                  </div>
                </div>
              )}
              
              {isRegistering && (
                <div className="input-group">
                  <label>Email Address</label>
                  <input
                    type="email"
                    name="email"
                    placeholder="Enter your email"
                    value={form.email}
                    onChange={handleInputChange}
                    required
                  />
                </div>
              )}
              
              <div className="input-group">
                <label>Username</label>
                <input
                  type="text"
                  name="username"
                  placeholder="Enter your username"
                  value={form.username}
                  onChange={handleInputChange}
                  required
                />
              </div>
              
              <div className="input-group">
                <label>Password</label>
                <input
                  type="password"
                  name="password"
                  placeholder="Enter your password"
                  value={form.password}
                  onChange={handleInputChange}
                  required
                />
              </div>
              
              {!isRegistering && (
                <div className="form-options">
                  <label className="checkbox-container">
                    <input
                      type="checkbox"
                      name="rememberMe"
                      checked={form.rememberMe}
                      onChange={handleInputChange}
                    />
                    <span className="checkmark"></span>
                    Remember me
                  </label>
                  <a href="#forgot" className="forgot-link">Forgot password?</a>
                </div>
              )}
              
              <button type="submit" className="auth-button">
                {isRegistering ? 'Create Account' : 'Sign In'}
              </button>
            </form>
            
            <div className="auth-footer">
              <p>
                {isRegistering ? 'Already have an account?' : "Don't have an account?"}
                <button type="button" className="toggle-form-btn" onClick={toggleFormMode}>
                  {isRegistering ? 'Sign In' : 'Register Now'}
                </button>
              </p>
            </div>
            
            {!isRegistering && (
              <div className="social-auth">
                <div className="divider">
                  <span>Or continue with</span>
                </div>
                <div className="social-buttons">
                  <button className="social-btn google-btn">
                    <i className="icon-google"></i>
                    Google
                  </button>
                  <button className="social-btn facebook-btn">
                    <i className="icon-facebook"></i>
                    Facebook
                  </button>
                </div>
              </div>
            )}
          </div>
        ) : (
          <div className="account-section">
            <div className="account-header">
              <div className="user-avatar">
                {user.firstName.charAt(0)}{user.lastName.charAt(0)}
              </div>
              <h2>Welcome, {user.firstName}!</h2>
              <p>Member since {user.joinDate}</p>
            </div>
            
            <div className="account-tabs">
              <button 
                className={activeTab === 'profile' ? 'tab active' : 'tab'}
                onClick={() => setActiveTab('profile')}
              >
                Profile
              </button>
              <button 
                className={activeTab === 'orders' ? 'tab active' : 'tab'}
                onClick={() => setActiveTab('orders')}
              >
                Orders
              </button>
              <button 
                className={activeTab === 'settings' ? 'tab active' : 'tab'}
                onClick={() => setActiveTab('settings')}
              >
                Settings
              </button>
            </div>
            
            <div className="tab-content">
              {activeTab === 'profile' && (
                <div className="profile-info">
                  <div className="info-item">
                    <span className="label">Username:</span>
                    <span className="value">{user.username}</span>
                  </div>
                  <div className="info-item">
                    <span className="label">Email:</span>
                    <span className="value">{user.email}</span>
                  </div>
                  <div className="info-item">
                    <span className="label">Name:</span>
                    <span className="value">{user.firstName} {user.lastName}</span>
                  </div>
                  <div className="info-item">
                    <span className="label">Member since:</span>
                    <span className="value">{user.joinDate}</span>
                  </div>
                </div>
              )}
              
              {activeTab === 'orders' && (
                <div className="orders-info">
                  <h3>Order History</h3>
                  <div className="order-stats">
                    <div className="stat-card">
                      <span className="stat-number">{user.orders}</span>
                      <span className="stat-label">Total Orders</span>
                    </div>
                    <div className="stat-card">
                      <span className="stat-number">$1,245.00</span>
                      <span className="stat-label">Total Spent</span>
                    </div>
                  </div>
                  <div className="recent-orders">
                    <p>Your recent orders will appear here.</p>
                  </div>
                </div>
              )}
              
              {activeTab === 'settings' && (
                <div className="settings-info">
                  <h3>Account Settings</h3>
                  <div className="setting-item">
                    <label>Email Notifications</label>
                    <label className="switch">
                      <input type="checkbox" defaultChecked />
                      <span className="slider"></span>
                    </label>
                  </div>
                  <div className="setting-item">
                    <label>Marketing Emails</label>
                    <label className="switch">
                      <input type="checkbox" />
                      <span className="slider"></span>
                    </label>
                  </div>
                  <div className="setting-item">
                    <label>Two-Factor Authentication</label>
                    <label className="switch">
                      <input type="checkbox" />
                      <span className="slider"></span>
                    </label>
                  </div>
                </div>
              )}
            </div>
            
            <button onClick={handleLogout} className="logout-btn">
              Sign Out
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default MyAccount;
