import React, { useState, useEffect, useRef } from 'react';
import Layout from '../components/Layout/Layout';
import './Home.css';

function Home() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const featureRefs = useRef([]);

  // Sample slider data
  const sliderData = [
    {
      id: 1,
      title: "Excellence in Education",
      subtitle: "Nurturing young minds for a brighter future",
      image: "https://cdn.pixabay.com/photo/2022/03/04/13/01/school-children-7047286_1280.jpg"
    },
    {
      id: 2,
      title: "Modern Learning Environment",
      subtitle: "State-of-the-art facilities for optimal learning",
      image: "https://images.unsplash.com/photo-1591123120675-6f7f1aae0e5b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1800&q=80"
    },
    {
      id: 3,
      title: "Building Community",
      subtitle: "Where students grow together",
      image: "https://images.unsplash.com/photo-1577896851231-70ef18881754?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1800&q=80"
    }
  ];

  // Auto slide functionality
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % sliderData.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [sliderData.length]);

  useEffect(() => {
    // Add 3D animation on scroll for features
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('animate-feature');
          }
        });
      },
      { threshold: 0.3 }
    );

    featureRefs.current.forEach((ref) => {
      if (ref) observer.observe(ref);
    });

    return () => {
      const currentRefs = featureRefs.current; // Copy ref to a variable
      if (currentRefs) {
        currentRefs.forEach((ref) => {
          // Cleanup logic
        });
      }
    };
  }, []);

  return (
    <Layout>
      <div className="home-container">
        {/* Hero Slider Section */}
        <section className="hero-slider">
          <div className="slider-wrapper">
            {sliderData.map((slide, index) => (
              <div 
                key={slide.id}
                className={`slide ${index === currentSlide ? 'active' : ''}`}
                style={{ backgroundImage: `url(${slide.image})` }}
              >
                <div className="slide-content">
                  <h1 className="slide-title">{slide.title}</h1>
                  <p className="slide-subtitle">{slide.subtitle}</p>
                  <div className="slide-buttons">
                    <button className="btn-primary">Learn More</button>
                    <button className="btn-secondary">Apply Now</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="slider-indicators">
            {sliderData.map((_, index) => (
              <button
                key={index}
                className={`indicator ${index === currentSlide ? 'active' : ''}`}
                onClick={() => setCurrentSlide(index)}
              ></button>
            ))}
          </div>
        </section>

        {/* Welcome Section */}
        <section className="welcome-section">
          <div className="container">
            <div className="welcome-content">
              <h2>Welcome to Our Prestigious School</h2>
              <div className="divider"></div>
              <p>Where we inspire excellence, nurture potential, and build character. Our dedicated faculty and state-of-the-art facilities provide an environment where students can thrive academically, socially, and personally.</p>
              <div className="stats-container">
                <div className="stat">
                  <h3>25+</h3>
                  <p>Years of Excellence</p>
                </div>
                <div className="stat">
                  <h3>2,000+</h3>
                  <p>Happy Students</p>
                </div>
                <div className="stat">
                  <h3>150+</h3>
                  <p>Qualified Teachers</p>
                </div>
                <div className="stat">
                  <h3>15+</h3>
                  <p>Acres Campus</p>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Features Section - Enhanced */}
        <section className="features-section">
          <div className="container">
            <h2>Why Choose Our School</h2>
            <div className="divider"></div>
            <div className="features">
              <div 
                className="feature" 
                ref={el => featureRefs.current[0] = el}
                style={{ '--hue': '210' }}
              >
                <div className="feature-image">
                  <img 
                    src="https://images.unsplash.com/photo-1501504905252-473c47e087f8?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80" 
                    alt="Academic Excellence" 
                  />
                </div>
                <h3>Academic Excellence</h3>
                <p>Our curriculum is designed to challenge students and foster critical thinking skills.</p>
              </div>

              <div 
                className="feature" 
                ref={el => featureRefs.current[1] = el}
                style={{ '--hue': '190' }}
              >
                <div className="feature-image">
                  <img 
                    src="https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80" 
                    alt="Sports Programs" 
                  />
                </div>
                <h3>Sports Programs</h3>
                <p>We offer a wide range of athletic activities to promote physical health and teamwork.</p>
              </div>

              <div 
                className="feature" 
                ref={el => featureRefs.current[2] = el}
                style={{ '--hue': '220' }}
              >
                <div className="feature-image">
                  <img 
                    src="https://images.unsplash.com/photo-1541961017774-22349e4a1262?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80" 
                    alt="Arts & Culture" 
                  />
                </div>
                <h3>Arts & Culture</h3>
                <p>Our arts programs encourage creativity and self-expression in various mediums.</p>
              </div>

              <div 
                className="feature" 
                ref={el => featureRefs.current[3] = el}
                style={{ '--hue': '200' }}
              >
                <div className="feature-image">
                  <img 
                    src="https://images.unsplash.com/photo-1523580494863-6f3031224c94?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=500&q=80" 
                    alt="Inclusive Community" 
                  />
                </div>
                <h3>Inclusive Community</h3>
                <p>We celebrate diversity and foster an environment of respect and inclusion.</p>
              </div>
            </div>
          </div>
        </section>

        {/* Poster Section */}
        <section className="poster-section">
          <div className="poster-content">
            <div className="poster-text">
              <h2>Transforming Education for Tomorrow's Leaders</h2>
              <p>Our innovative approach to learning prepares students not just for exams, but for life. With cutting-edge technology and proven teaching methodologies, we create an engaging educational experience.</p>
              <button className="btn-primary">Discover Our Programs</button>
            </div>
            <div className="poster-image">
              <img src="https://images.unsplash.com/photo-1546410531-bb4caa6b424d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1800&q=80" alt="Students in classroom" />
            </div>
          </div>
        </section>

        {/* Testimonials Section */}
        <section className="testimonials-section">
          <div className="container">
            <h2>What Parents & Students Say</h2>
            <div className="divider"></div>
            <div className="testimonials">
              <div className="testimonial">
                <div className="testimonial-content">
                  <p>"This school has transformed my child's learning experience. The teachers are dedicated and the facilities are exceptional."</p>
                </div>
                <div className="testimonial-author">
                  <h4>Sarah Johnson</h4>
                  <p>Parent of Grade 5 Student</p>
                </div>
              </div>
              <div className="testimonial">
                <div className="testimonial-content">
                  <p>"I've grown so much since joining this school. The supportive environment helped me discover my passion for science."</p>
                </div>
                <div className="testimonial-author">
                  <h4>Michael Chen</h4>
                  <p>Grade 11 Student</p>
                </div>
              </div>
              <div className="testimonial">
                <div className="testimonial-content">
                  <p>"As an educator, I appreciate the school's commitment to innovative teaching methods and student-centered learning."</p>
                </div>
                <div className="testimonial-author">
                  <h4>Dr. Emily Rodriguez</h4>
                  <p>Science Department Head</p>
                </div>
              </div>
            </div>
          </div>
        </section>

        {/* Call to Action Section */}
        <section className="cta-section">
          <div className="container">
            <div className="cta-content">
              <h2>Ready to Join Our Community?</h2>
              <p>Schedule a visit to our campus or begin the application process today.</p>
              <div className="cta-buttons">
                <button className="btn-primary">Apply Now</button>
                <button className="btn-secondary">Schedule a Tour</button>
              </div>
            </div>
          </div>
        </section>
      </div>
    </Layout>
  );
}

export default Home;
