import React, { useEffect } from 'react';
import './InnovativeLearning.css';

const InnovativeLearning = () => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <div className="innovative-learning">
      <div className="hero-section" style={{ backgroundImage: `url('https://images.unsplash.com/photo-1584697964358-3e14ca57658b?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80')` }}>
        <div className="hero-overlay"></div>
        <div className="hero-content">
          <h1>Innovative Learning</h1>
          <p>Integrating technology to prepare students for the digital future.</p>
        </div>
      </div>
      <div className="content-section">
        <div className="text-content">
          <h2>Our Approach</h2>
          <p>We use smart boards, 1:1 device programs, and interactive learning tools to enhance the educational experience.</p>
          <ul>
            <li>Smart Boards</li>
            <li>1:1 Device Programs</li>
            <li>Interactive Learning Tools</li>
          </ul>
        </div>
        <div className="image-content">
          <img src="https://images.unsplash.com/photo-1584697964358-3e14ca57658b?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80" alt="Innovative Learning" />
        </div>
      </div>
    </div>
  );
};

export default InnovativeLearning;
