import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isAccountDropdownOpen, setIsAccountDropdownOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false); // Mock login state
  const [user, setUser] = useState(null); // Mock user data
  const navigate = useNavigate();

  useEffect(() => {
    // Mock authentication check
    const checkAuthStatus = () => {
      // In a real app, this would check localStorage or make an API call
      const mockUser = localStorage.getItem('eduprime_user');
      if (mockUser) {
        setIsLoggedIn(true);
        setUser(JSON.parse(mockUser));
      }
    };

    checkAuthStatus();

    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleAccountDropdown = () => {
    setIsAccountDropdownOpen(!isAccountDropdownOpen);
  };

  const handleLogout = () => {
    // Mock logout functionality
    localStorage.removeItem('eduprime_user');
    setIsLoggedIn(false);
    setUser(null);
    setIsAccountDropdownOpen(false);
    navigate('/');
  };

  const handleLogin = () => {
    // Mock login functionality
    const mockUser = {
      name: 'John Doe',
      email: 'john.doe@example.com',
      avatar: 'https://via.placeholder.com/40'
    };
    localStorage.setItem('eduprime_user', JSON.stringify(mockUser));
    setIsLoggedIn(true);
    setUser(mockUser);
    navigate('/profile');
  };

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
      <div className="header-container">
        <div className="logo">
          <Link to="/">
            <h1>EduPrime</h1>
          </Link>
        </div>

        <nav className={`navigation ${isMenuOpen ? 'nav-open' : ''}`}>
          <ul>
            <li><Link to="/" onClick={() => setIsMenuOpen(false)}>Home</Link></li>
            <li><Link to="/about" onClick={() => setIsMenuOpen(false)}>About</Link></li>
            <li><Link to="/admissions" onClick={() => setIsMenuOpen(false)}>Admissions</Link></li>
            <li><Link to="/academics" onClick={() => setIsMenuOpen(false)}>Academics</Link></li>
            <li><Link to="/contact" onClick={() => setIsMenuOpen(false)}>Contact</Link></li>
          </ul>
        </nav>

        <div className="account-section">
          {isLoggedIn ? (
            <div className="account-dropdown">
              <button className="account-toggle" onClick={toggleAccountDropdown}>
                <img src={user.avatar} alt={user.name} className="user-avatar" />
                <span>{user.name}</span>
                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width="16" height="16">
                  <path d="M12 14l-4-4h8z" fill="currentColor"/>
                </svg>
              </button>
              {isAccountDropdownOpen && (
                <div className="dropdown-menu">
                  <Link to="/profile" onClick={() => setIsAccountDropdownOpen(false)}>Profile</Link>
                  <Link to="/settings" onClick={() => setIsAccountDropdownOpen(false)}>Settings</Link>
                  <Link to="/grades" onClick={() => setIsAccountDropdownOpen(false)}>Grades</Link>
                  <button onClick={handleLogout}>Logout</button>
                </div>
              )}
            </div>
          ) : (
            <div className="auth-buttons">
              <button className="login-btn" onClick={handleLogin}>Login</button>
              <button className="signup-btn" onClick={() => navigate('/register')}>Sign Up</button>
            </div>
          )}
        </div>

        <button className={`hamburger ${isMenuOpen ? 'open' : ''}`} onClick={toggleMenu}>
          <span></span>
          <span></span>
          <span></span>
        </button>
      </div>
    </header>
  );
};

export default Header;
