import React, { useState, useEffect } from 'react';
import './Home.css';

const Home = () => {
  // Hero slider state
  const [currentSlide, setCurrentSlide] = useState(0);

  // Sample data for hero slider
  const sliderData = [
    {
      id: 1,
      image: "https://images.unsplash.com/photo-1523050854058-8df90110c9f1?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80",
      title: "Welcome to Bright Future Academy",
      subtitle: "Excellence in Education Since 1995"
    },
