import React, { useState, useEffect } from 'react';
import './ProgramsDetail.css';

const ProgramsDetail = () => {
  const [activeProgram, setActiveProgram] = useState(null);

  useEffect(() => {
    // Add scroll animation on component mount
    const handleScroll = () => {
      const elements = document.querySelectorAll('.program-card, .programs-hero');
      elements.forEach(element => {
        const elementTop = element.getBoundingClientRect().top;
        const windowHeight = window.innerHeight;
        if (elementTop < windowHeight * 0.85) {
          element.classList.add('visible');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Initial check

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const programs = [
    {
      id: 1,
      title: "STEM Program",
      description: "Advanced science, technology, engineering, and mathematics curriculum designed to challenge and inspire students.",
      longDescription: "Our STEM program offers a comprehensive curriculum that integrates hands-on learning with theoretical knowledge. Students engage in robotics, coding, advanced mathematics, and scientific research projects. Our state-of-the-art laboratories and experienced instructors provide an environment where innovation thrives.",
      image: "https://images.unsplash.com/photo-1560785496-3c9d27877182?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
      icon: "ðŸ”¬",
      features: ["Robotics Club", "Coding Workshops", "Science Fairs", "Math Olympiad Training"],
      ageGroup: "Ages 10-18"
    },
    {
      id: 2,
      title: "Arts Academy",
      description: "Comprehensive visual and performing arts education to foster creativity and self-expression.",
      longDescription: "The Arts Academy nurtures creative expression through diverse mediums including painting, sculpture, theater, dance, and music. Our professional artists and performers guide students in developing technical skills while encouraging their unique artistic voice. Students showcase their work in annual exhibitions and performances.",
      image: "https://images.unsplash.com/photo-1577896851231-70ef18881754?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
      icon: "ðŸŽ¨",
      features: ["Visual Arts Studio", "Theater Productions", "Music Composition", "Digital Arts"],
      ageGroup: "Ages 8-16"
    },
    {
      id: 3,
      title: "Language Immersion",
      description: "Full language immersion programs in Spanish, French, and Mandarin with native-speaking instructors.",
      longDescription: "Our Language Immersion program creates an authentic environment for language acquisition through cultural experiences, conversation practice, and project-based learning. Students develop fluency while gaining appreciation for diverse cultures and global perspectives.",
      image: "https://images.unsplash.com/photo-1503875154399-95f4665d7eb3?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
      icon: "ðŸŒŽ",
      features: ["Native Speaking Teachers", "Cultural Events", "Language Certifications", "Study Abroad Opportunities"],
      ageGroup: "Ages 5-15"
    },
    {
      id: 4,
      title: "Sports Excellence",
      description: "Professional coaching in various sports with focus on skill development, teamwork, and healthy competition.",
      longDescription: "The Sports Excellence program combines athletic training with character development. Our certified coaches focus on technical skills, physical conditioning, sportsmanship, and leadership. Facilities include regulation-sized fields, courts, and a fully-equipped fitness center.",
      image: "https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80",
      icon: "âš½",
      features: ["Professional Coaching", "Competition Teams", "Fitness Training", "Sports Psychology"],
      ageGroup: "Ages 8-18"
    }
  ];

  return (
    <div className="programs-detail">
      <div className="programs-hero" style={{ backgroundImage: `url('https://images.unsplash.com/photo-1523050854058-8df90110c9f1?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80')` }}>
        <div className="hero-overlay"></div>
        <div className="hero-content">
          <h1 className="hero-title">Our Programs</h1>
          <p className="hero-subtitle">Explore the diverse programs we offer to nurture young minds and unlock their potential</p>
          <div className="scroll-indicator">
            <span></span>
          </div>
        </div>
      </div>

      <div className="programs-container">
        <div className="programs-intro">
          <h2>Shaping Future Leaders</h2>
          <p>Our carefully designed programs combine academic excellence with character development, preparing students for success in an ever-changing world. Each program is tailored to specific age groups and interests, ensuring every child finds their passion.</p>
        </div>

        <div className="programs-grid">
          {programs.map(program => (
            <div 
              key={program.id} 
              className={`program-card ${activeProgram === program.id ? 'active' : ''}`}
              onMouseEnter={() => setActiveProgram(program.id)}
              onMouseLeave={() => setActiveProgram(null)}
            >
              <div className="card-header">
                <div className="program-icon">{program.icon}</div>
                <h3>{program.title}</h3>
                <span className="age-group">{program.ageGroup}</span>
              </div>
              <div className="card-image">
                <img src={program.image} alt={program.title} />
                <div className="image-overlay"></div>
              </div>
              <div className="card-content">
                <p>{program.description}</p>
                <ul className="program-features">
                  {program.features.map((feature, index) => (
                    <li key={index}>{feature}</li>
                  ))}
                </ul>
              </div>
              <div className="card-actions">
                <button className="learn-more-btn">Learn More</button>
              </div>
            </div>
          ))}
        </div>

        {activeProgram && (
          <div className="program-detail-modal">
            <div className="modal-content">
              <h2>{programs.find(p => p.id === activeProgram).title}</h2>
              <p>{programs.find(p => p.id === activeProgram).longDescription}</p>
              <button className="close-modal" onClick={() => setActiveProgram(null)}>Ã—</button>
            </div>
          </div>
        )}

        <div className="programs-cta">
          <h2>Ready to Explore Further?</h2>
          <p>Schedule a visit to our campus or speak with our program advisors to find the perfect fit for your child.</p>
          <div className="cta-buttons">
            <button className="cta-btn primary">Schedule a Tour</button>
            <button className="cta-btn secondary">Contact Us</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProgramsDetail;
